import React, { useState } from 'react'

function Form() {

    const [name, setName] = useState('');
    const [email, setEmail] = useState();
    const [task, setTask] = useState([]);

    const addTask = () => {
        if (name.trim()) {
            setTask([...task, name]);
            setTask([...email, name]);
            setName("");
            setEmail("");
        }
    };


    return (
        <div>
            <input type="text" placeholder='Enter Name' value={name} onChange={(e) => setTask(e.target.value)} />
            <input type="text" placeholder='Enter E-mail' value={email} onChange={(e) => setTask(e.target.value)} />
            <button onClick={addTask}>Submit</button>

            <ul>
                {
                    task.map((i,j)=>(
                        <li key={j}>
                            {i.name}{i.email}
                        </li>
                    ))
                }
            </ul>
        </div >
    )
}

export default Form;
