import React, { useState } from 'react'

export default function Todo1() {

    const [text, setText] = useState("")
    const [list, setList] = useState([])
    const [edit, setEdit] = useState(null)

    function handelsubmit(e){
        setText(e.target.value)
    }

    function handeladd(index){
        if(edit !== null){
            const updaeList = [...list]
            updaeList[edit] = text
            setList(updaeList)
            setEdit(null)
        }else{
            setList([...list,text])
        }
        setText("")
    }

    function handeledit(index){
        setText(list[index])
        setEdit(index)
    }
    
    function handelDelete(index){
        const updaeList = [...list]
        updaeList.splice[index,1]
        setList(updaeList)
    }

    return (

        <div>

            <input type="text" name="" id="" placeholder='Enter The Task' value={text} onChange={handelsubmit} />
            <button onClick={handeladd}>Add</button>


            <ul>
                {list.map((i, index) => (
                    <li key={index} >{i}
                        <button onClick={()=>handeledit}>Edit</button>
                        <button onClick={()=>handelDelete}>Delete</button>
                    </li>
                ))}
            </ul>

        </div>
    )
}
