import { useState } from "react";

function Todo() {
  const [text, setText] = useState()
  const [list, setList] = useState([])
  const [edit, setEdit] = useState(null)

  function handelsubmit(e) {
    setText(e.target.value)
  }

  function handeladd(index) {

    if (edit !== null) {
      const updateList = [...list]
      updateList[edit] = text
      setList(updateList)
      setEdit(null)
    } else {
      setList([...list, text])

    }
    setText("")
  }

  function handeledit(index) {
    setText(list[index])
    setEdit(index)
  }

  function handelDelete(index) {
    const updateList = [...list];
    updateList.splice(index, 1);
    setList(updateList)
  }
  

  return (
    <div>
      <input type="text" name="" id="" value={text} onChange={handelsubmit} placeholder="Enter name" />
      <button onClick={handeladd}>Add</button>

      <ul>
        {list.map((i, index) => (
          <li key={index}>{i}
            <button onClick={() => handeledit(index)}>Edit</button>
            <button onClick={() => handelDelete(index)}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  )
}

export default Todo;
