import React, { useEffect, useRef, useState } from "react";

const Eventhandler = () => {
    const [text, setText] = useState("");
    const [second, setSecond] = useState(0);
    const [res, setRes] = useState(false);
    const timer = useRef(null);

    function handlesubmit(t) {
        setText(t.target.value);
        if (!timer.current) {
            timer.current = setInterval(() => {
                setSecond((s) => s + 1);
            }, 1000);
        }
    }
    function add() {
        setRes(true);
        clearInterval(timer.current);
        timer.current = null;
        setTimeout(() => {
            setText("");
            setSecond(0);
            setRes(false);
        }, 9000);
    }
    let char = text.length;
    return (
        <div>
            <textarea placeholder="start the typing..."
                value={text}
                onChange={handlesubmit}
                className="p-3 w-25 mt-5 ms-5"
                style={{ height: "220px" }}
            ></textarea>
            <br></br>
            <button onClick={add} className="p-2 ms-5  btn btn-dark fs-3">
                Submit
            </button>
            {res && (
                <>
                    <h3 className="ms-4 mt-4">Seconds:{second}</h3>
                    <h3 className="ms-4 mt-3">Character:{char}</h3>
                </>
            )}
        </div>
    );
};

export default Eventhandler;
