import React, { useState } from "react";

function Explorer({ modeType }) {
  const styles = {
    backgroundColor: modeType === 'dark' ? '#1e1e2f' : '#ffffff',
    color: modeType === 'dark' ? '#ffffff' : '#111111',
    padding: '30px',
    borderRadius: '12px',
    textAlign: 'center',
    transition: '0.3s',
  };

  return (
    <div style={{ display: 'flex', justifyContent: 'center', margin: '30px 0' }}>
      <div style={styles}>
        <h2>{modeType === 'dark' ? 'Dark Mode' : 'Light Mode'}</h2>
        <p>This is a simple theme box using props.</p>
      </div>
    </div>
  );
}

export function ExplorerCalculator() {
  const [value, setValue] = useState('');

  const handleMultiply = () => {
    setValue(Number(value) * 5);
  };

  return (
    <div style={{ display: 'flex', justifyContent: 'center', marginTop: '30px' }}>
      <div style={{
        padding: '20px',
        border: '2px solid black',
        borderRadius: '10px',
        backgroundColor: '#fff',
        textAlign: 'center'
      }}>
        <h3>{value}</h3>
        <input
          type="text"
          placeholder="Enter value"
          value={value}
          onChange={(e) => setValue(e.target.value)}
          style={{ padding: '8px', marginRight: '10px' }}
        />
        <button onClick={handleMultiply} style={{ padding: '8px 16px' }}>
          Multiply
        </button>
      </div>
    </div>
  );
}

export default Explorer;
