import React, { useState } from "react";

function DarkMode({ modeType }) {
  const themeStyles = {
    light: {
      backgroundColor: '#ffffff',
      color: '#111111',
      padding: '40px',
      borderRadius: '15px',
      textAlign: 'center',
      transition: 'all 0.3s ease',
    },
    dark: {
      backgroundColor: '#1e1e2f',
      color: '#ffffff',
      padding: '40px',
      borderRadius: '15px',
      textAlign: 'center',
      transition: 'all 0.3s ease',
    }
  };

  return (
    <div
      style={{
        minHeight: '40vh',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
      }}
    >
      <div style={themeStyles[modeType]}>
        <h2>{modeType === 'dark' ? 'Dark Mode' : 'Light Mode'}</h2>
        <p>This is a clean and simple theme box using props in React.</p>
      </div>
    </div>
  );
}

export default DarkMode;
