import React, { useRef } from 'react'

export const Formval = () => {
    const nameRef = useRef(null)
    const emailRef = useRef(null)
    const errorRef = useRef(null)
    const succesRef = useRef(null)
    const password = useRef(null)

    function handlSubmit(e) {
        e.preventDefault()
        const name = nameRef.current.value
        const email = emailRef.current.value


        if (!name || !email) {
            errorRef.current.innerHTML = 'fill the form'
            succesRef.current.innerHTML = ''

        }
        if (!/\S+@\S+\.\S+/.test(email)) {
            errorRef.current.innerText = " Please enter a valid email address.";
            succesRef.current.innerText = "";

        }
        else {
            succesRef.current.innerHTML = 'succes'
            errorRef.current.innerHTML = ''
        }

    }


    return (
        <div>
            <form onSubmit={handlSubmit}>
                <input type="text" name="name" ref={nameRef} placeholder='Enter The Name'/>
                <input type="email" name="email" ref={emailRef} placeholder=''/>
                <input type="pass" name="password" ref={password} />
                <button type='submit'>submit</button>
            </form>
            <p ref={errorRef} ></p>
            <p ref={succesRef}></p>
        </div>
    )
}
