import React, { useRef } from 'react';

export const Formval = () => {
    const nameRef = useRef(null);
    const emailRef = useRef(null);
    const passwordRef = useRef(null);
    const errorRef = useRef(null);
    const successRef = useRef(null);

    const handleSubmit = (e) => {
        e.preventDefault();

        const name = nameRef.current.value.trim();
        const email = emailRef.current.value.trim();
        const password = passwordRef.current.value.trim();

        // Clear previous messages
        errorRef.current.innerText = '';
        successRef.current.innerText = '';

        // Basic form validation
        if (!name || !email || !password) {
            errorRef.current.innerText = 'Please fill in all fields.';
            return;
        }

        // Email validation
        const emailPattern = /\S+@\S+\.\S+/;
        if (!emailPattern.test(email)) {
            errorRef.current.innerText = 'Please enter a valid email address.';
            return;
        }

        // All validations passed
        successRef.current.innerText = 'Success! Form submitted.';
    };

    return (
        <div style={{
            textAlign: 'center',
            margin: '200px',
             height: '40px',
            margin: '10px 0',
            padding: '5px',
            width: '250px',
            fontSize: '16px'
        }}>
            <form onSubmit={handleSubmit}>
                <input type="text" name="name" ref={nameRef} placeholder="Name" style={inputStyle} /><br />
                <input type="email" name="email" ref={emailRef} placeholder="Email" style={inputStyle} /><br />
                <input type="password" name="password" ref={passwordRef} placeholder="Password" style={inputStyle} /><br />
                <button type="submit" style={{ height: '40px', width: '100px', fontSize: '16px' }}>Submit</button>
            </form>
            <p ref={errorRef} style={{ color: 'red' }}></p>
            <p ref={successRef} style={{ color: 'green' }}></p>
        </div>
    );
};
