import React, { useState } from 'react'

function Form() {

    const [task, setTask] = useState('');
    const [tasks, setTasks] = useState([]);

    const addTask = () => {
        if (task.trim()) {
            setTasks([...tasks, task]);
            setTask("");
        }
    };


    return (
        <div>
            <input type="text" placeholder='Enter Name' value={task} onChange={(e) => (e.target.value)} />
            <input type="email" placeholder='Enter E-mail' value={task} onChange={(e) => (e.target.value)} />
            <button onCanPlay={addTask}>Submit</button>

            <ul>
                li
            </ul>
        </div>
    )
}

export default Form;
