import React, { useState } from "react";

function Todo() {

  const [task, setTask] = useState('');
  const [tasks, setTasks] = useState();


  const addTask = () => {
    if (task.trim()) {
      setTasks([...tasks, task]);
      setTask("");
    }
  };

  return (
    <div>

      <h1>Todo List</h1>
      <input type="text" placeholder="Enter Name" value={task} onChange={(e) => setTask(e.target.value)} />
      <button onClick={addTask}>Add</button>
    </div>
  )
}

export default Todo;
