import React, { useState } from 'react';

export default function Input() {
    const [isStyled, setIsStyled] = useState(false);

    const handleClick = () => {
        setIsStyled(true);
    };

    return (
        <div>
            <p
                style={{
                    color: isStyled ? 'red' : 'black',
                    backgroundColor: isStyled ? 'black' : 'transparent',
                    padding: '10px',
                }}
            >
                Lorem ipsum dolor sit amet consectetur, adipisicing elit. Accusamus ab, eius voluptatem quam et repellendus quae laudantium, reiciendis autem adipisci maiores inventore. Magnam quaerat hic sequi accusantium voluptatibus qui labore.
            </p>
            <button onClick={handleClick}>Submit</button>
        </div>
    );
}
