import React, { useState } from "react";

function DarkMode({ modeType }) {
  const themeStyles = {
    light: {
      backgroundColor: '#ffffff',
      color: '#111111',
      padding: '40px',
      borderRadius: '15px',
      textAlign: 'center',
      transition: 'all 0.3s ease',
    },
    dark: {
      backgroundColor: '#1e1e2f',
      color: '#ffffff',
      padding: '40px',
      borderRadius: '15px',
      textAlign: 'center',
      transition: 'all 0.3s ease',
    }
  };

  return (
    <div
      style={{
        minHeight: '40vh',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
      }}
    >
      <div style={themeStyles[modeType]}>
        <h2>{modeType === 'dark' ? 'Dark Mode' : 'Light Mode'}</h2>
        <p>This is a clean and simple theme box using props in React.</p>
      </div>
    </div>
  );
}

export function DarkCalculator() {
  const [text, setText] = useState('');

  const multiplication = () => {
    setText(Number(text) * 5);
  };

  return (
    <div style={styles.container}>
      <div style={styles.box}>
        <h1 style={styles.heading}>{text}</h1>
        <input
          type="text"
          placeholder="Enter Value"
          value={text}
          onChange={(e) => setText(e.target.value)}
          style={styles.input}
        />
        <button onClick={multiplication} style={styles.button}>
          Count
        </button>
      </div>
    </div>
  );
}

const styles = {
  container: {
    display: 'flex',
    // height: '100vh',
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: 'rgb(189, 211, 255)'
  },
  box: {
    padding: '30px',
    borderRadius: '12px',
    backgroundColor: '#fff',
    boxShadow: '0 4px 15px rgba(0,0,0,0.1)',
    textAlign: 'center',
    border: '2px solid black'
  },
  heading: {
    marginBottom: '20px',
    color: '#333',
  },
  input: {
    padding: '10px',
    fontSize: '16px',
    borderRadius: '8px',
    border: '3px solid black',
    width: '200px',
    marginRight: '10px',
  },
  button: {
    padding: '10px 20px',
    backgroundColor: 'red',
    color: 'white',
    border: '3px solid black',
    borderRadius: '8px',
    fontSize: '16px',
    cursor: 'pointer',
  },
};

export default DarkMode;
