import React, { useState } from 'react'

export default function Counterf() {

  const [task,setTask] = useState("")
  const [tasks,setTasks] = useState([])
  const [editIndex,setEditIndex] = useState(null)

  const Text = () =>{
    if (!task.trim())return;

    setTask(
      editIndex === null ? [...tasks,task] : tasks.map((t,i)=>i === editIndex ? task : t)
    );
    setTask("");
    setEditIndex(null);
  }

  return (
    <div>
      
      <input type="text" name='name' placeholder='Enter The Task' value={task} onClick={Text}/>
      <input type="Submit" />

    </div>
  )
}
