import React, { useState } from 'react'

function Form() {

    const [task, setTask] = useState('');
    const [tasks, setTasks] = useState([]);
    // const [email,setEmail] = useState([]);

    const addTask = () => {
        if (task.trim()) {
            setTasks([...tasks, task]);
            setTask("");
        }
    };


    return (
        <div>
            <input type="text" placeholder='Enter Name'name='' value={task} onChange={(e) => setTask(e.target.value)} />
            {/* <input type="email" placeholder='Enter E-mail' value={task} onChange={(e) => setTask(e.target.value)} /> */}
            <button onCanPlay={addTask}>Submit</button>

            <ul>
                {tasks.map((item, index) => (
                    <li key={index}>
                        {item}
                    {/* <button onClick={() => handlEdit(index)}>Edit</button>{ ""} */}
                    {/* <button onClick={() => handlDelete(index)}>Delete</button> */}
                    </li>
                    ))}
        </ul>
        </div >
    )
}

export default Form;
