// import Demo from './componet/demo';
// import Create from 'import './App.css';

import { useEffect, useState } from 'react';
import DarkMode from './componet/DarkMode';

function App() {
  const [currentTheme, setCurrentTheme] = useState('light');

  const handleToggleTheme = () => {
    setCurrentTheme(prevTheme => (prevTheme === 'light' ? 'dark' : 'light'));
  };

  useEffect(() => {
    document.body.style.backgroundColor = currentTheme === 'dark' ? '#121212' : '#f9f9f9';
  }, [currentTheme]);

  return (
    <div style={{
      textAlign: 'center',
      padding: '30px',
      transition: 'all 0.3s ease',
    }}>
      <h1 style={{ marginBottom: '20px', color: currentTheme === 'dark' ? '#ffffff' : '#222222' }}>
        üîÅ Theme Switcher App
      </h1>

      <button
        onClick={handleToggleTheme}
        style={{
          padding: '10px 20px',
          fontSize: '16px',
          backgroundColor: currentTheme === 'light' ? '#333333' : '#ffffff',
          color: currentTheme === 'light' ? '#ffffff' : '#333333',
          border: 'none',
          borderRadius: '8px',
          cursor: 'pointer',
          transition: 'all 0.3s ease',
          marginBottom: '30px'
        }}
      >
        Switch to {currentTheme === 'light' ? 'Dark' : 'Light'} Mode
      </button>

      <DarkMode modeType={currentTheme} />
    </div>
  );
}

export default App;
