import React, { useState } from "react";

function Demo() {
    // Array of product objects
    const [products, setProducts] = useState([
        { id: 1, name: "Mobile", price: 150, image: "https://pisces.bbystatic.com/image2/BestBuy_US/images/products/6487/6487480_sd.jpg" },
        { id: 2, name: "Laptop", price: 500, image: "https://p4-ofp.static.pub//fes/cms/2025/06/09/rzx1vjodhuhbacc1btsrj85uhocghm157825.png" },
        { id: 3, name: "Pen", price: 50, image: "https://via.placeholder.com/50?text=Pen" },
        { id: 4, name: "Book", price: 120, image: "https://via.placeholder.com/50?text=Book" },
    ]);

    // Filter products with price > 100
    const filteredProducts = products.filter((product) => product.price > 100);

    // State for editing
    const [editingProduct, setEditingProduct] = useState(null);
    const [editValues, setEditValues] = useState({ name: '', price: '', image: '' });

    // Handle product click
    const handleProductClick = (product) => {
        setEditingProduct(product.id);
        setEditValues({ name: product.name, price: product.price, image: product.image });
    };

    // Handle input change
    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setEditValues((prev) => ({ ...prev, [name]: value }));
    };

    // Handle save
    const handleSave = () => {
        setProducts((prev) =>
            prev.map((p) =>
                p.id === editingProduct
                    ? { ...p, name: editValues.name, price: Number(editValues.price), image: editValues.image }
                    : p
            )
        );
        setEditingProduct(null);
    };

    return (
        <>
            <ul>
                {filteredProducts.map((product) => (
                    <li
                        key={product.id}
                        style={{ display: 'flex', alignItems: 'center', gap: '10px', cursor: 'pointer' }}
                        onClick={() => handleProductClick(product)}
                    >
                        <img src={product.image} alt={product.name} width={50} height={50} />
                        <span>{product.name} - â‚¹{product.price}</span>
                    </li>
                ))}
            </ul>
            <button onClick={() => {
                const newId = products.length + 1;
                setProducts([
                    ...products,
                    {
                        id: newId,
                        name: `Product${newId}`,
                        price: 100 + newId * 10,
                        image: `https://via.placeholder.com/50?text=Product${newId}`
                    }
                ]);
            }}>
                Product Add
            </button>
            {editingProduct && (
                <div style={{ marginTop: '20px' }}>
                    <h3>Edit Product</h3>
                    <input
                        type="text"
                        name="name"
                        value={editValues.name}
                        onChange={handleInputChange}
                        placeholder="Product Name"
                    />
                    <input
                        type="number"
                        name="price"
                        value={editValues.price}
                        onChange={handleInputChange}
                        placeholder="Price"
                        style={{ marginLeft: '10px' }}
                    />
                    <input
                        type="text"
                        name="image"
                        value={editValues.image}
                        onChange={handleInputChange}
                        placeholder="Image URL"
                        style={{ marginLeft: '10px', width: '250px' }}
                    />
                    <button onClick={handleSave} style={{ marginLeft: '10px' }}>Save</button>
                </div>
            )}
        </>
    );
}

export default Demo;
