import React, { useState } from "react";

function DarkMode({ modeType }) {
  const themeStyles = {
    light: {
      backgroundColor: '#ffffff',
      color: '#111111',
      padding: '40px',
      borderRadius: '15px',
      textAlign: 'center',
      transition: 'all 0.3s ease',
    },
    dark: {
      backgroundColor: '#1e1e2f',
      color: '#ffffff',
      padding: '40px',
      borderRadius: '15px',
      textAlign: 'center',
      transition: 'all 0.3s ease',
    }
  };

  return (
    <div
      style={{
        minHeight: '40vh',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
      }}
    >
      <div style={themeStyles[modeType]}>
        <h2>{modeType === 'dark' ? 'Dark Mode' : 'Light Mode'}</h2>
        <p>This is a clean and simple theme box using props in React.</p>
      </div>
    </div>
  );
}

export function DarkCalculator() {
  const [text, setText] = useState('');

  const multiplication = () => {
    setText(Number(text) * 5);
  };

  const styles = {
    container: {
      display: 'flex',
      justifyContent: 'center',
      alignItems: 'center',
      marginTop: '20px',
    },
    box: {
      padding: '20px',
      backgroundColor: '#f0f0f0',
      borderRadius: '10px',
      textAlign: 'center',
      width: '300px',
    },
    heading: {
      marginBottom: '10px',
    },
    input: {
      padding: '10px',
      width: '100%',
      borderRadius: '5px',
      border: '1px solid #ccc',
    },
  };

  return (
    <div style={styles.container}>
      <div style={styles.box}>
        <h1 style={styles.heading}>{text}</h1>
        <input
          type="text"
          placeholder="Enter Value"
          value={text}
          onChange={(e) => setText(e.target.value)}
          style={styles.input}
        />
        <button onClick={multiplication} style={{ marginTop: '10px', padding: '10px' }}>
          Multiply by 5
        </button>
      </div>
    </div>
  );
}

export default DarkMode;
