import React, { useState, useEffect } from "react";

function DarkMode() {
  const [theme, setTheme] = useState('light');
  const [count, setCount] = useState(0);

  const toggleTheme = () => {
    setTheme((prev) => (prev === 'light' ? 'dark' : 'light'));
  };

  useEffect(() => {
    document.body.style.backgroundColor = theme === 'dark' ? '#121212' : '#f9f9f9';
  }, [theme]);

  const increment = () => setCount(count + 1);
  const decrement = () => setCount(count > 0 ? count - 1 : 0);
  const reset = () => setCount(0);

  const themeBoxStyle = {
    backgroundColor: theme === 'dark' ? '#1e1e2f' : '#ffffff',
    color: theme === 'dark' ? '#ffffff' : '#111111',
    padding: '40px',
    borderRadius: '15px',
    textAlign: 'center',
    marginBottom: '20px',
    transition: 'all 0.3s ease',
  };

  const buttonStyle = {
    padding: '10px 20px',
    margin: '10px',
    backgroundColor: theme === 'dark' ? '#fff' : '#333',
    color: theme === 'dark' ? '#000' : '#fff',
    border: 'none',
    borderRadius: '8px',
    cursor: 'pointer',
  };

  return (
    <div style={{ textAlign: 'center', padding: '30px' }}>
      <h1 style={{ color: theme === 'dark' ? '#fff' : '#000' }}>Theme Switcher App</h1>

      <button onClick={toggleTheme} style={buttonStyle}>
        Switch to {theme === 'light' ? 'Dark' : 'Light'} Mode
      </button>

      {/* Theme Box */}
      <div style={themeBoxStyle}>
        <h2>{theme === 'dark' ? 'Dark Mode' : 'Light Mode'}</h2>
        <p>This is a clean and simple theme box using props in React.</p>
      </div>

      {/* Counter */}
      <div style={themeBoxStyle}>
        <h2>Counter: {count}</h2>
        <button onClick={increment} style={buttonStyle}>+</button>
        <button onClick={decrement} style={buttonStyle}>-</button>
        <button onClick={reset} style={buttonStyle}>Reset</button>
      </div>
    </div>
  );
}

export default DarkMode;
