import React, { useState, useEffect } from "react";
import DarkMode, { DarkCalculator } from "./Darkmode";
import Counter from "./Counter"; // Agar Counter aapka alag component hai

function Dark() {
  const [theme, setTheme] = useState('light');

  const toggleTheme = () => {
    setTheme(prev => (prev === 'light' ? 'dark' : 'light'));
  };

  useEffect(() => {
    document.body.style.backgroundColor = theme === 'dark' ? '#121212' : '#f9f9f9';
  }, [theme]);

  return (
    <div style={{ textAlign: 'center', padding: '30px' }}>
      <h1 style={{ color: theme === 'dark' ? '#fff' : '#000' }}>Theme Switcher App</h1>

      <button
        onClick={toggleTheme}
        style={{
          padding: '10px 20px',
          marginTop: '20px',
          backgroundColor: theme === 'dark' ? '#fff' : '#333',
          color: theme === 'dark' ? '#000' : '#fff',
          border: 'none',
          borderRadius: '8px',
          cursor: 'pointer'
        }}
      >
        Switch to {theme === 'light' ? 'Dark' : 'Light'} Mode
      </button>

      <DarkMode modeType={theme} />
      <DarkCalculator />
      <Counter />
    </div>
  );
}

export default Dark;
